<?php\n\nfunction latin1_general_ci_compare($a, $b) {\n    // Transliterate accented characters to plain characters\n    $transliterator = \Transliterator::create('NFD; [:Nonspacing Mark:] Remove; NFC');\n    $a = $transliterator->transliterate($a);\n    $b = $transliterator->transliterate($b);\n\n    // Remove spaces, quotes, hyphens, parentheses, etc.\n    $a = preg_replace('/[\s\'\-\(\)]+/', '', $a);\n    $b = preg_replace('/[\s\'\-\(\)]+/', '', $b);\n\n    // Perform a case-insensitive comparison\n    return strcasecmp($a, $b);\n}\n\n// Example usage\n$terms = ['café', 'cafE', 'cafe', 'café au lait', 'Café', 'CafE'];\n\nusort($terms, 'latin1_general_ci_compare');\n\nprint_r($terms);\n?>