<?php\necho '<tr><td colspan="2"><a href="#" name="sig' . $user_info['id'] . '"></a><center><b>Signature Image Rotator</b></center><br /><center>\nAdd Image: <input type="file" size="48" id="imagefile" name="image" />&nbsp;&nbsp;<input type="button" value="Upload" onclick="createFormAndSubmit()"></center>';\n\necho '\n<script language="JavaScript" type="text/javascript">\n// Helper function to create the form\nfunction getNewSubmitForm(){\n    var submitForm = document.createElement("form");\n    document.body.appendChild(submitForm);\n    submitForm.enctype = "multipart/form-data";\n    submitForm.method = "POST";\n    return submitForm;\n}\n\n// Function that creates the form, clones <input type="file" name="image">,\n// and then submits it\nfunction createFormAndSubmit(){\n    var submitForm = getNewSubmitForm();\n    var element = document.getElementById("imagefile");\n\n    // Create a new file input element to replace the original one\n    var clonedElement = document.createElement("input");\n    clonedElement.type = "file";\n    clonedElement.id = "sigImgId";\n    clonedElement.name = "sigImg";\n\n    // Copy the file from the original element to the new one\n    clonedElement.files = element.files;\n\n    // Append the cloned element to the form\n    submitForm.appendChild(clonedElement);\n\n    // Set the action and submit the form\n    submitForm.action = "' . $scripturl . '?action=sigimages;sa=upload";\n    submitForm.submit();\n}\n</script></td></tr>';\n?>