$writer = new XMLWriter();\n$writer->openMemory();\n$writer->setIndent(true);\n\n// Start the document\n$writer->startDocument('1.0', 'UTF-8');\n\n// Declare namespaces at the root element or where necessary\n$writer->startElementNS('ns1', 'root', 'http://example.com/ns1');\n$writer->writeAttributeNS('xmlns', 'ns2', null, 'http://example.com/ns2');\n\n// Add elements using the declared namespaces\n$writer->startElement('ns1:child1');\n$writer->text('Content of child 1');\n$writer->endElement();\n\n$writer->startElement('ns2:child2');\n$writer->text('Content of child 2');\n$writer->endElement();\n\n// End the root element\n$writer->endElement();\n\n// End the document\n$writer->endDocument();\n\n// Output the resulting XML\necho $writer->outputMemory();