$colId = new Zend_Form_Element_MultiCheckbox('colId');\n$colId->setLabel('Col ID')\n      ->setMultiOptions(array_flip(array('sadda', 'asss')));\n\n// Disable specific options\n$disabledOptions = array('sadda'); // Specify the options you want to disable\n\nforeach ($colId->getMultiOptions() as $value => $label) {\n    if (in_array($value, $disabledOptions)) {\n        $colId->setAttrib('disabled', 'disabled');\n    }\n}\n\n// Custom rendering to add disabled attribute to the specific options\n$colId->addDecorator('ViewHelper', array('helper' => 'formMultiCheckbox', 'placement' => 'APPEND'));\n\nclass My_View_Helper_FormMultiCheckbox extends Zend_View_Helper_FormMultiCheckbox\n{\n    public function formMultiCheckbox($name, $value = null, $attribs = null, $options = null, $listsep = "<br />\n")\n    {\n        if (isset($attribs['disabledOptions'])) {\n            $disabledOptions = $attribs['disabledOptions'];\n            unset($attribs['disabledOptions']);\n        } else {\n            $disabledOptions = array();\n        }\n\n        $content = '';\n        foreach ($options as $opt_value => $opt_label) {\n            $opt_attribs = $attribs;\n            if (in_array($opt_value, $disabledOptions)) {\n                $opt_attribs['disabled'] = 'disabled';\n            }\n            $content .= $this->view->formCheckbox($name . '[]', $opt_value, $opt_attribs, $opt_value) . $opt_label . $listsep;\n        }\n\n        return $content;\n    }\n}\n\n// Add custom decorator with disabled options\n$colId->setDecorators(array(\n    array('ViewHelper', array('helper' => 'FormMultiCheckbox', 'disabledOptions' => $disabledOptions))\n));\n\n// Add the element to the form\n$form = new Zend_Form();\n$form->addElement($colId);