<?php\nrequire_once('lib/nusoap.php'); // Include the NuSOAP library\n\n$server = new nusoap_server();\n$server->configureWSDL('fileService', 'urn:fileService');\n\n// Register the method to receive the file\n$server->register('receiveFile', \n    array('fileName' => 'xsd:string', 'fileData' => 'xsd:base64Binary'), \n    array('return' => 'xsd:string'), \n    'urn:fileService', \n    'urn:fileService#receiveFile', \n    'rpc', \n    'encoded', \n    'Receives a file and saves it to the server'\n);\n\nfunction receiveFile($fileName, $fileData) {\n    $filePath = 'uploads/' . basename($fileName);\n    $fileBytes = base64_decode($fileData);\n    \n    if (file_put_contents($filePath, $fileBytes) !== false) {\n        return "File uploaded successfully";\n    } else {\n        return "Failed to upload file";\n    }\n}\n\n$HTTP_RAW_POST_DATA = isset($HTTP_RAW_POST_DATA) ? $HTTP_RAW_POST_DATA : '';\n$server->service($HTTP_RAW_POST_DATA);\n?>