<?php\nrequire_once('lib/nusoap.php'); // Include the NuSOAP library\n\n$client = new nusoap_client('http://yourserver.com/server.php?wsdl', 'wsdl');\n\n// Check for any errors\n$err = $client->getError();\nif ($err) {\n    echo 'Constructor error: ' . $err;\n    exit();\n}\n\n// Read the file and convert it to a byte stream\n$fileName = 'example.txt';\n$fileData = file_get_contents($fileName);\n$fileDataBase64 = base64_encode($fileData);\n\n// Call the receiveFile method\n$result = $client->call('receiveFile', array('fileName' => $fileName, 'fileData' => $fileDataBase64));\n\n// Check for any errors\nif ($client->fault) {\n    echo 'Fault: ';\n    print_r($result);\n} else {\n    $err = $client->getError();\n    if ($err) {\n        echo 'Error: ' . $err;\n    } else {\n        echo 'Result: ' . $result;\n    }\n}\n?>