<?php\n// Define the command to execute the shell script\n$cmd = 'RUNMYSCRIPT.sh';\n\n// Define descriptor specifications\n$descriptorspec = array(\n    0 => array("pipe", "r"),  // stdin is a pipe that the child will read from\n    1 => array("pipe", "w"),  // stdout is a pipe that the child will write to\n    2 => array("pipe", "w")   // stderr is a pipe that the child will write to\n);\n\n// Open the process\n$process = proc_open($cmd, $descriptorspec, $pipes);\n\n// Check if the process is successfully opened\nif (is_resource($process)) {\n    // Let the script run for 10 seconds\n    sleep(10);\n    \n    // Send the termination key to the script\n    fwrite($pipes[0], 'q');\n    fclose($pipes[0]);\n    \n    // Close the other pipes\n    fclose($pipes[1]);\n    fclose($pipes[2]);\n\n    // Close the process\n    $return_value = proc_close($process);\n\n    echo "Command returned $return_value\n";\n} else {\n    echo "Failed to open process\n";\n}\n?>