<?php\n// Start time\n$start_time = microtime(true);\n\n// Initialize query counter\n$query_count = 0;\n\n// Function to execute a query and increment the counter\nfunction execute_query($query, $connection) {\n    global $query_count;\n    $query_count++;\n    return mysqli_query($connection, $query);\n}\n\n// Your database connection\n$connection = mysqli_connect('host', 'username', 'password', 'database');\n\n// Example queries\nexecute_query("SELECT * FROM table1", $connection);\nexecute_query("SELECT * FROM table2", $connection);\n\n// End time\n$end_time = microtime(true);\n\n// Calculate the difference\n$page_load_time = $end_time - $start_time;\n\n// Display the page load time and query count\necho "Page loaded in " . $page_load_time . " seconds.<br>";\necho "Number of SQL queries: " . $query_count;\n?>