// Constructing the Zend_Db_Select object\n$select = $db->select()\n    ->from(\n        'users',\n        array(\n            'user_id', \n            'name', \n            'gender', \n            'city', \n            'province', \n            'country', \n            'image_id', \n            'one_liner', \n            'self_description', \n            'reputation'\n        )\n    )\n    ->where(\n        "(69.1 * (latitude - ?)) * (69.1 * (latitude - ?)) + \n        (69.1 * (longitude - ?) * COS(? / 57.3)) * \n        (69.1 * (longitude - ?) * COS(? / 57.3)) < ?", \n        array($user->latitude, $user->latitude, $user->longitude, $user->latitude, $user->longitude, $user->latitude, pow($radius, 2))\n    )\n    ->order(\n        "(69.1 * (latitude - ?)) * (69.1 * (latitude - ?)) + \n        (69.1 * (longitude - ?) * COS(? / 57.3)) * \n        (69.1 * (longitude - ?) * COS(? / 57.3)) ASC",\n        array($user->latitude, $user->latitude, $user->longitude, $user->latitude, $user->longitude, $user->latitude)\n    );\n\n// Constructing the Zend_Paginator object\n$paginator = Zend_Paginator::factory($select);\n$paginator->setItemCountPerPage(10); // Set items per page\n$paginator->setCurrentPageNumber($currentPage); // Set current page number\n\n// Fetch the paginated results\n$results = $paginator->getCurrentItems();