<?php\n// Initialize a cURL session\n$ch = curl_init();\n\n// Set the URL you want to retrieve\ncurl_setopt($ch, CURLOPT_URL, 'http://example.com');\n\n// Set the range to the first 10,000 bytes\ncurl_setopt($ch, CURLOPT_RANGE, '0-9999');\n\n// Return the transfer as a string instead of outputting it directly\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Execute the cURL request and fetch the content\n$response = curl_exec($ch);\n\n// Check for errors\nif($response === false) {\n    echo 'Curl error: ' . curl_error($ch);\n} else {\n    echo 'Data retrieved successfully';\n    // Do something with the $response, e.g., echo it, save it, etc.\n}\n\n// Close the cURL session\ncurl_close($ch);\n?>