class Controller_A_Controller extends Controller {\n\n    // Assuming this is your index method\n    public function action_index($method = null) {\n        if ($method) {\n            // Handle the call to non-existing methods\n            // $method contains the name of the non-existing method\n            echo "Called non-existing method: " . $method;\n        } else {\n            // Normal index action logic\n            echo "This is the index method.";\n        }\n    }\n\n    // Magic method to catch calls to non-existing methods\n    public function __call($method, $args) {\n        $this->action_index($method);\n    }\n}