class Input {\n    public function get($name, $default = '') {\n        return isset($_GET[$name]) ? $_GET[$name] : $default;\n    }\n\n    public function post($name, $default = '') {\n        return isset($_POST[$name]) ? $_POST[$name] : $default;\n    }\n\n    public function cookies($name, $default = '') {\n        return isset($_COOKIE[$name]) ? $_COOKIE[$name] : $default;\n    }\n\n    public function input($name, $default = '') {\n        if (isset($_GET[$name])) {\n            return $_GET[$name];\n        } elseif (isset($_POST[$name])) {\n            return $_POST[$name];\n        } else {\n            return $default;\n        }\n    }\n}\n\n// Usage\n$valueGet = $this->input->get('name', '');\n$valuePost = $this->input->post('name', '');\n$valueCookie = $this->input->cookies('name', '');\n$valueInput = $this->input->input('name', 'default value if not set');