<?php\n// Load the animated GIF\n$gif = new Imagick('path/to/your/animated.gif');\n\n// Resize the GIF\nforeach ($gif as $frame) {\n    $frame->thumbnailImage(200, 0); // Resize width to 200 pixels, maintain aspect ratio\n}\n\n// Optimize the GIF (optional)\n$gif = $gif->coalesceImages();\n$gif->optimizeImageLayers();\n\n// Save the resized GIF\n$gif->writeImages('path/to/your/resized_animated.gif', true);\n\n// Clear the resources\n$gif->clear();\n$gif->destroy();\n?>