// Assuming you have the $mail object containing the email data\n$mail = $imapTransport->fetchByMessageNr(1); // Fetch the first message\n\n// Check if the message body is a multipart report\nif ($mail[0] instanceof ezcMailMultipartReport) {\n    $multipartReport = $mail[0];\n    \n    // Iterate through the parts\n    foreach ($multipartReport->parts as $part) {\n        if ($part instanceof ezcMailText) {\n            // Output the text part of the message\n            echo $part->text;\n        }\n    }\n}