$sql = 'SELECT photographers.photographer_id, photographers.photographer_name FROM photographers';\n\n$stmt = $conn->stmt_init();\nif ($stmt->prepare($sql)) {\n    $stmt->execute();\n    $stmt->bind_result($photographer_id, $photographer_name);\n    \n    $results = array();\n    \n    while ($stmt->fetch()) {\n        $results[] = array(\n            'photographer_id' => $photographer_id,\n            'photographer_name' => $photographer_name\n        );\n    }\n    \n    $stmt->close();\n}\n\n// You can now loop through the $results array to access the data\nforeach ($results as $row) {\n    echo "ID: " . $row['photographer_id'] . " Name: " . $row['photographer_name'] . "<br>";\n}