<?php\nif ($cmd == "addcat") {\n    // Define a whitelist of allowed columns\n    $allowedColumns = ['fakeapproved', 'fakesuspected', 'keyword', 'parallelimport'];\n    \n    // Validate the category against the whitelist\n    if (in_array($category, $allowedColumns)) {\n        // Construct the SQL query dynamically\n        $alterQuery = "UPDATE auctions SET $category = true WHERE article_no= ?";\n        \n        if ($altRecord = $con->prepare($alterQuery)) {\n            $altRecord->bind_param("s", $pk);\n            $altRecord->execute();\n            $altRecord->close();\n        } else {\n            echo "false";\n        }\n\n        // Insert into users table\n        $insertQuery = "INSERT INTO users (username, $category) VALUES (?, true) \n                        ON DUPLICATE KEY UPDATE $category = true";\n        \n        if ($insRecord = $con->prepare($insertQuery)) {\n            $insRecord->bind_param("s", $username);\n            $insRecord->execute();\n            $insRecord->close();\n            echo "true";\n        } else {\n            echo "false";\n        }\n    } else {\n        echo "Invalid category";\n    }\n}\n?>