// Retrieve and sanitize the 'pwd' input from POST data\n$pwd = filter_input(INPUT_POST, 'pwd', FILTER_SANITIZE_STRING);\n\n// Check if the input was successfully retrieved and sanitized\nif ($pwd === null || $pwd === false) {\n    // Handle the error, for example, by showing an error message or logging it\n    echo "Invalid input.";\n} else {\n    // Proceed with using the sanitized input\n    echo "Password: " . $pwd;\n}