// Receive the data\n     $encodedData = $_POST['data'];\n     \n     // Decode Base64 data\n     $encryptedData = base64_decode($encodedData);\n     \n     // Save the encrypted data to a file\n     $filePath = "/tmp/encrypted_data.asc";\n     file_put_contents($filePath, $encryptedData);\n     \n     // Decrypt the data using GnuPG\n     $gpg = "/usr/bin/gpg";\n     $command = "$gpg --decrypt $filePath";\n     $decryptedData = shell_exec($command);\n     \n     // Use the decrypted data as needed\n     echo "Decrypted data: " . htmlentities($decryptedData);