function create_custom_post_type() {\n    register_post_type('custom_post',\n        array(\n            'labels' => array(\n                'name' => __('Custom Posts'),\n                'singular_name' => __('Custom Post')\n            ),\n            'public' => true,\n            'has_archive' => true,\n            'supports' => array('title', 'editor', 'comments')\n        )\n    );\n}\nadd_action('init', 'create_custom_post_type');