function save() {\n    /* \n        Here we do either a create or\n        update operation depending \n        on the value of the id field.\n        Zero means create, non-zero\n        update\n    */\n\n    $this->title = htmlspecialchars($this->title, ENT_QUOTES, 'UTF-8');\n    $this->description = htmlspecialchars($this->description, ENT_QUOTES, 'UTF-8');\n\n    try {\n        $db = parent::getConnection();\n        if ($this->id == 0) {\n            $query = 'INSERT INTO articles (modified, username, url, title, description, points) VALUES (NOW(), ?, ?, ?, ?, ?)';\n            $stmt = $db->prepare($query);\n            $stmt->execute([$this->username, $this->url, $this->title, $this->description, $this->points]);\n            createRSS(); //**** rss function****\n        } else if ($this->id != 0) {\n            $query = 'UPDATE articles SET modified = NOW(), username = ?, url = ?, title = ?, description = ?, points = ?, ranking = ? WHERE id = ?';\n            $stmt = $db->prepare($query);\n            $stmt->execute([$this->username, $this->url, $this->title, $this->description, $this->points, $this->ranking, $this->id]);\n        }\n\n        $lastid = $db->lastInsertId();\n\n        if ($this->id == 0) {\n            $this->id = $lastid;\n        }\n\n    } catch (Exception $e) {\n        throw $e;\n    }\n}