// Assume $pdo is a PDO instance connected to your database\n$stmt = $pdo->prepare("INSERT INTO session_data (session_id, data) VALUES (:session_id, :data)");\n$stmt->execute(['session_id' => session_id(), 'data' => 'some temporary data']);\n\n// Retrieve\n$stmt = $pdo->prepare("SELECT data FROM session_data WHERE session_id = :session_id");\n$stmt->execute(['session_id' => session_id()]);\n$temp_data = $stmt->fetchColumn();