class MyClass {\n    public function methodWithConstant($configValue) {\n        // Use $configValue instead of directly accessing the constant\n        echo $configValue;\n    }\n\n    public function methodWithGetVar($getVar) {\n        // Use $getVar instead of directly accessing $_GET\n        if (isset($getVar)) {\n            echo $getVar;\n        } else {\n            echo 'GET variable not set';\n        }\n    }\n}\n\n// Usage\n$configValue = MY_CONSTANT; // Assuming MY_CONSTANT is defined\n$getVar = isset($_GET['var']) ? $_GET['var'] : null;\n\n$instance = new MyClass();\n$instance->methodWithConstant($configValue);\n$instance->methodWithGetVar($getVar);