<?php\n\nfunction convertToUserTimezone($timestamp, $timezoneOffset) {\n    // Create a DateTime object from the timestamp\n    $date = new DateTime($timestamp, new DateTimeZone('UTC'));\n    \n    // Convert offset to hours and minutes for DateTimeZone\n    $offset = intval($timezoneOffset);\n    $hours = floor($offset);\n    $minutes = ($offset - $hours) * 60;\n\n    // Construct the timezone string\n    $timezoneString = sprintf("%+03d:%02d", $hours, $minutes);\n\n    // Create a DateTimeZone object with the user's timezone\n    $userTimezone = new DateTimeZone($timezoneString);\n\n    // Set the timezone to the user's timezone\n    $date->setTimezone($userTimezone);\n\n    // Format the date as needed\n    return $date->format('d, l Y. h:ia');\n}\n\n// Example usage\n$timestamp = '2023-10-01 15:00:00'; // Example timestamp\n$users_timezone_code = '+10.5'; // Example timezone offset\n\n$convertedDate = convertToUserTimezone($timestamp, $users_timezone_code);\necho $convertedDate;\n\n?>