<?php\nfunction callApi($url) {\n    $ch = curl_init();\n\n    // Set cURL options\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n    curl_setopt($ch, CURLOPT_TIMEOUT, 10); // Timeout after 10 seconds\n    curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 5); // Timeout after 5 seconds if cannot connect\n\n    $response = curl_exec($ch);\n\n    // Check for errors\n    if (curl_errno($ch)) {\n        $error_msg = curl_error($ch);\n        // Handle the error appropriately\n        error_log('cURL error: ' . $error_msg);\n        curl_close($ch);\n        return false;\n    }\n\n    // Check for HTTP status codes\n    $http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n    if ($http_code != 200) {\n        // Handle the non-200 responses\n        error_log('API request failed with HTTP code: ' . $http_code);\n        curl_close($ch);\n        return false;\n    }\n\n    curl_close($ch);\n    return $response;\n}\n\n// Example usage\n$url = 'https://api.twitter.com/2/tweets'; // Example Twitter API endpoint\n$response = callApi($url);\n\nif ($response === false) {\n    // Handle the failure to call the API\n    echo "API call failed.";\n} else {\n    // Process the API response\n    echo "API response: " . $response;\n}\n?>