<?php\nfunction getDistance($origin, $destination, $mode = 'driving') {\n    $apiKey = 'YOUR_API_KEY_HERE'; // Replace with your Google Maps API key\n    $url = "https://maps.googleapis.com/maps/api/distancematrix/json?units=metric&origins=" . urlencode($origin) . "&destinations=" . urlencode($destination) . "&mode=$mode&key=$apiKey";\n\n    // Initialize cURL session\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    \n    // Execute cURL request\n    $response = curl_exec($ch);\n    curl_close($ch);\n\n    $data = json_decode($response, true);\n\n    if ($data['status'] == 'OK') {\n        $distance = $data['rows'][0]['elements'][0]['distance']['text'];\n        $duration = $data['rows'][0]['elements'][0]['duration']['text'];\n        return array('distance' => $distance, 'duration' => $duration);\n    } else {\n        return false;\n    }\n}\n\n// Example usage\n$origin = 'New York, NY';\n$destination = 'Los Angeles, CA';\n$result = getDistance($origin, $destination, 'walking');\n\nif ($result) {\n    echo 'Distance: ' . $result['distance'] . "\n";\n    echo 'Duration: ' . $result['duration'] . "\n";\n} else {\n    echo 'An error occurred.';\n}\n?>