// When the user selects a language\n$language = 'en'; // this would be the user-selected language\nsetcookie('user_language', $language, time() + (86400 * 30), "/"); // 86400 = 1 day, cookie set for 30 days\n\n// To retrieve the language\nif(isset($_COOKIE['user_language'])) {\n    $user_language = $_COOKIE['user_language'];\n} else {\n    // Fallback language\n    $user_language = 'default_language';\n}