use PHPMailer\PHPMailer\PHPMailer;\nuse PHPMailer\PHPMailer\Exception;\n\nrequire 'path/to/PHPMailer/src/Exception.php';\nrequire 'path/to/PHPMailer/src/PHPMailer.php';\nrequire 'path/to/PHPMailer/src/SMTP.php';\n\n$mail = new PHPMailer(true);\n\ntry {\n    //Server settings\n    $mail->isSMTP();\n    $mail->Host       = 'smtp.bol.com.br'; // Set the SMTP server to send through\n    $mail->SMTPAuth   = true;\n    $mail->Username   = 'your-email@bol.com.br'; // SMTP username\n    $mail->Password   = 'your-email-password'; // SMTP password\n    $mail->SMTPSecure = PHPMailer::ENCRYPTION_STARTTLS;\n    $mail->Port       = 587;\n\n    //Recipients\n    $mail->setFrom('your-email@bol.com.br', 'Your Name'); // Ensure this matches the Username\n    $mail->addAddress('deltrem1984@bol.com.br'); // Add a recipient\n    \n    // Content\n    $mail->isHTML(true);\n    $mail->Subject = 'Here is the subject';\n    $mail->Body    = 'This is the HTML message body <b>in bold!</b>';\n    $mail->AltBody = 'This is the body in plain text for non-HTML mail clients';\n\n    $mail->send();\n    echo 'Message has been sent';\n} catch (Exception $e) {\n    echo "Message could not be sent. Mailer Error: {$mail->ErrorInfo}";\n}