<?php\n// Get all categories\n$categories = get_categories(array(\n    'orderby' => 'name',\n    'order'   => 'ASC'\n));\n\n// Initialize an array to hold categories by their first letter\n$alphabetical_categories = array();\n\nforeach ($categories as $category) {\n    $first_letter = strtoupper(substr($category->name, 0, 1));\n    if (!isset($alphabetical_categories[$first_letter])) {\n        $alphabetical_categories[$first_letter] = array();\n    }\n    $alphabetical_categories[$first_letter][] = $category;\n}\n\n// Display categories alphabetically\nforeach ($alphabetical_categories as $letter => $cats) {\n    echo '<p>' . $letter . '</p>';\n    echo '<ul>';\n    foreach ($cats as $cat) {\n        echo '<li>---' . $cat->name . '</li>';\n    }\n    echo '</ul>';\n}\n?>