<?php\n   // add_book.php\n   include 'database.php';\n\n   if ($_SERVER["REQUEST_METHOD"] == "POST") {\n       $title = $_POST['title'];\n       $author = $_POST['author'];\n       $current_stage = $_POST['current_stage'];\n       $estimated_completion_date = $_POST['estimated_completion_date'];\n\n       $sql = "INSERT INTO books (title, author, current_stage, estimated_completion_date) VALUES (:title, :author, :current_stage, :estimated_completion_date)";\n       $stmt = $pdo->prepare($sql);\n\n       $stmt->execute(['title' => $title, 'author' => $author, 'current_stage' => $current_stage, 'estimated_completion_date' => $estimated_completion_date]);\n\n       echo "Book added successfully.";\n   }\n   ?>