<?php\n$request = $_SERVER['REQUEST_URI'];\n\nswitch ($request) {\n    case '/newpage' :\n        require __DIR__ . '/newpage.php';\n        break;\n    case preg_match('/^\/another-page\/([0-9]+)$/', $request, $matches) ? true : false:\n        $_GET['id'] = $matches[1];\n        require __DIR__ . '/another-page.php';\n        break;\n    default:\n        // Handle 404 error or redirect\n        http_response_code(404);\n        require __DIR__ . '/404.php';\n        break;\n}\n?>