<?php\n$css = $_POST['css'];\n$shopName = strip_tags($_POST['title']);\n$email = $_POST['email'];\n\n$con = new mysqli("hostname", "username", "password", "database");\n\nif ($con->connect_error) {\n    die('Could not connect to database: ' . $con->connect_error);\n}\n\n$stmt = $con->prepare("INSERT INTO wps_Shops (shopName, shopEmail, shopStyle) VALUES (?, ?, ?)");\n$stmt->bind_param("sss", $shopName, $email, $css);\n\nif ($stmt->execute()) {\n    $last_id = $stmt->insert_id;\n    echo ("<h1>Shop created</h1>");\n    echo ("<p><a href=\"shop.php?id=$last_id\">Go to shop</a></p>");\n    echo ("<p><a href=\"addproducts.php?id=$last_id\">Add products</a></p>");\n} else {\n    echo ("Error: " . $stmt->error);\n}\n\n$stmt->close();\n$con->close();\n?>