<?php\n// Load the XML file\n$xml = new DOMDocument();\n$xml->load('file.xml');\n\n// Retrieve the content of the <entry> tag\n$entries = $xml->getElementsByTagName('entry');\n\n// Loop through each entry and display its content\nforeach ($entries as $entry) {\n    // Import the node to avoid issues with different document contexts\n    $importedNode = $xml->importNode($entry, true);\n    \n    // Create a new DOMDocument to hold the entry content\n    $contentDoc = new DOMDocument();\n    $contentDoc->appendChild($contentDoc->importNode($importedNode, true));\n    \n    // Output the content preserving the XHTML formatting\n    echo $contentDoc->saveHTML();\n}\n?>