<?php\nfunction removeCarriageReturns($htmlContent) {\n    // Remove carriage returns (\r)\n    $htmlContent = str_replace("\r", "", $htmlContent);\n    // Remove newlines (\n)\n    $htmlContent = str_replace("\n", "", $htmlContent);\n    return $htmlContent;\n}\n\n// Example usage\n$htmlContent = file_get_contents('path/to/your/file.php');\n$cleanContent = removeCarriageReturns($htmlContent);\n\necho '<script type="text/javascript">\n    myJsFunction("'. addslashes($cleanContent) .'");\n</script>';\n?>