function getModulesControllersActions() {\n    $modules = [];\n    $modulesPath = APPLICATION_PATH . '/modules';\n\n    foreach (new DirectoryIterator($modulesPath) as $module) {\n        if ($module->isDot() || !$module->isDir()) {\n            continue;\n        }\n\n        $moduleName = $module->getFilename();\n        $modules[$moduleName] = [];\n\n        $controllersPath = $module->getPathname() . '/controllers';\n        if (!is_dir($controllersPath)) {\n            continue;\n        }\n\n        foreach (new DirectoryIterator($controllersPath) as $controller) {\n            if ($controller->isDot() || !$controller->isFile()) {\n                continue;\n            }\n\n            $controllerName = $controller->getFilename();\n            if (strpos($controllerName, 'Controller.php') === false) {\n                continue;\n            }\n\n            $controllerName = str_replace('Controller.php', '', $controllerName);\n            $modules[$moduleName][$controllerName] = [];\n\n            require_once $controller->getPathname();\n            $className = ucfirst($moduleName) . '_Controller_' . ucfirst($controllerName);\n            $reflection = new ReflectionClass($className);\n\n            foreach ($reflection->getMethods(ReflectionMethod::IS_PUBLIC) as $method) {\n                if (strpos($method->getName(), 'Action') !== false) {\n                    $actionName = str_replace('Action', '', $method->getName());\n                    $modules[$moduleName][$controllerName][] = $actionName;\n                }\n            }\n        }\n    }\n\n    return $modules;\n}\n\n// Example usage\n$modulesControllersActions = getModulesControllersActions();\nprint_r($modulesControllersActions);