include('header.php');\n\n$db->connect();\n\n$table = 'general_image';\n$page = 'general';\n$region = $_SESSION['region'];\n\n$action = '';\nif (isset($_POST['delete'])) {\n    $action = 'delete';\n} elseif (isset($_POST['active_switch'])) {\n    $action = 'active_switch';\n}\n\n$id = $secure->secure_string($_POST['id']);\n\n$max_active = 1;\n\nif ($action == 'active_switch' || $action == 'delete') {\n    $response = array();\n    $response['action'] = $action;\n    $response['id'] = $id;\n\n    if ($action == 'active_switch') {\n        $query = 'SELECT * FROM general WHERE region = "' . $region . '"';\n        $result = mysql_query($query);\n\n        if (!$result) {\n            $utility->fail($action, 'query error: ' . $query);\n        } else {\n            $row = mysql_fetch_assoc($result);\n            $already_active = false;\n            $active = '';\n\n            if ($id == $row['fifi']) {\n                $already_active = true;\n            }\n\n            $active .= $row['fifi'];\n            $response['results'] = $active;\n\n            if (!$already_active) {\n                $update = '';\n                $active = '';\n                $update .= 'fifi="' . $id . '"';\n                $active .= $id;\n                $query = 'UPDATE general SET ' . $update . ' WHERE region = "' . $region . '"';\n                $response['results'] = $active;\n            } else {\n                $utility->redirect($page . '.php');\n            }\n        }\n    } elseif ($action == 'delete') {\n        $query = "DELETE FROM $table WHERE id=" . $id;\n    }\n\n    echo $query . "hello";\n\n    if (!mysql_query($query)) {\n        $utility->fail($action, 'query error: ' . $query);\n    }\n}