public function loginAction()\n   {\n       $form = new Application_Form_Login(); // Assuming this is your form class\n       $this->view->form = $form;\n\n       if ($this->getRequest()->isPost()) {\n           $formData = $this->getRequest()->getPost();\n           if ($form->isValid($formData)) {\n               $auth = Zend_Auth::getInstance();\n               $authAdapter = $this->_getAuthAdapter($formData['userName'], $formData['password']);\n               $result = $auth->authenticate($authAdapter);\n\n               if ($result->isValid()) {\n                   // Successful authentication logic here\n               } else {\n                   // Add error message to form\n                   $form->addError('Username or password do not exist');\n                   $this->view->form = $form;\n               }\n           } else {\n               $form->populate($formData);\n           }\n       }\n   }