require_once 'Zend/Loader/Autoloader.php';\nZend_Loader_Autoloader::getInstance();\n\nuse Zend_Feed_Reader;\n\n$feedUrl = 'http://rss.example.com/feed';\n$feed = Zend_Feed_Reader::import($feedUrl);\n\necho $feed->getTitle() . "\n";\n\n// Check the type of the feed to infer the version\n$feedType = $feed->getType();\n\nif ($feedType === Zend_Feed_Reader::TYPE_RSS_20) {\n    echo "RSS Version: 2.0";\n} elseif ($feedType === Zend_Feed_Reader::TYPE_RSS_094) {\n    echo "RSS Version: 0.94";\n} elseif ($feedType === Zend_Feed_Reader::TYPE_RSS_091) {\n    echo "RSS Version: 0.91";\n} elseif ($feedType === Zend_Feed_Reader::TYPE_RSS_092) {\n    echo "RSS Version: 0.92";\n} elseif ($feedType === Zend_Feed_Reader::TYPE_ATOM_10) {\n    echo "Atom Version: 1.0";\n} elseif ($feedType === Zend_Feed_Reader::TYPE_ATOM_03) {\n    echo "Atom Version: 0.3";\n} else {\n    echo "Unknown Feed Type";\n}