<?php\n\nfunction fetchWikipediaInfobox($title) {\n    $url = "https://en.wikipedia.org/w/api.php?action=query&prop=revisions&rvprop=content&format=json&titles=" . urlencode($title) . "&rvsection=0";\n\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n    $output = curl_exec($ch);\n    curl_close($ch);\n\n    $data = json_decode($output, true);\n    $pages = $data['query']['pages'];\n    $page = reset($pages);\n    $content = $page['revisions'][0]['*'];\n\n    preg_match('/{{Infobox(.|\n)*?}}/', $content, $matches);\n    return $matches[0] ?? '';\n}\n\n$city = "Beijing";\n$country = "China";\n\n$cityInfobox = fetchWikipediaInfobox($city);\n$countryInfobox = fetchWikipediaInfobox($country);\n\necho "<h2>City Infobox</h2>";\necho "<pre>" . htmlspecialchars($cityInfobox) . "</pre>";\n\necho "<h2>Country Infobox</h2>";\necho "<pre>" . htmlspecialchars($countryInfobox) . "</pre>";\n\n?>