<?php\nif (isset($_GET['referrer_id'])) {\n    $querystringWithJunk = $_GET['referrer_id'];\n    $querystringArray = explode('/', $querystringWithJunk);\n\n    // Set cookie path and domain\n    $cookie_name = "referrer_id";\n    $cookie_value = $querystringArray[0];\n    $cookie_expiry = time() + 60*60*24*365*100; // 100 years\n    $cookie_path = "/"; // Available within the entire domain\n    $cookie_domain = ""; // Set to the current domain\n\n    // Set the cookie\n    setcookie($cookie_name, $cookie_value, $cookie_expiry, $cookie_path, $cookie_domain);\n    \n    echo "Cookie has been set.";\n}\n?>