<?php\nfunction isUsingProxy() {\n    $proxyHeaders = array(\n        'HTTP_VIA',\n        'HTTP_X_FORWARDED_FOR',\n        'HTTP_FORWARDED_FOR',\n        'HTTP_X_FORWARDED',\n        'HTTP_FORWARDED',\n        'HTTP_CLIENT_IP',\n        'HTTP_FORWARDED_FOR_IP',\n        'VIA',\n        'X_FORWARDED_FOR',\n        'FORWARDED_FOR',\n        'X_FORWARDED',\n        'FORWARDED',\n        'CLIENT_IP',\n        'FORWARDED_FOR_IP',\n    );\n\n    foreach ($proxyHeaders as $header) {\n        if (isset($_SERVER[$header])) {\n            return true;\n        }\n    }\n\n    // Additional check for anonymous proxies\n    $ip = $_SERVER['REMOTE_ADDR'];\n    $proxyCheckUrl = "http://proxycheck.io/v2/$ip?key=YOUR_API_KEY&vpn=1&asn=1";\n    $response = file_get_contents($proxyCheckUrl);\n    $proxyData = json_decode($response, true);\n\n    if ($proxyData['status'] === 'ok' && $proxyData['proxy'] === 'yes') {\n        return true;\n    }\n\n    return false;\n}\n\nif (isUsingProxy()) {\n    echo "Proxy detected!";\n} else {\n    echo "No proxy detected.";\n}\n?>