function p($expression) {\n    // Convert the expression to a string\n    $expression_str = var_export($expression, true);\n    // Evaluate the expression\n    $result = eval("return $expression_str;");\n    // Print the result\n    echo "$expression_str is $result\n";\n}\n\n// Example usage\n$i = 5;\np('$i'); // Outputs: '$i' is 5\np('1 + 2'); // Outputs: '1 + 2' is 3\np('$i * 2'); // Outputs: '$i * 2' is 10\n\n// Example with a function\nfunction factorial($n) {\n    return ($n === 0) ? 1 : $n * factorial($n - 1);\n}\np('3 * factorial(3)'); // Outputs: '3 * factorial(3)' is 18