/*******creates the month selection dropdown array****/\n\nfunction createMonths($id='month_select', $selected=null)\n{\n    /*** array of months ***/\n    $months = array(\n            1=>'Jan',\n            2=>'Feb',\n            3=>'Mar',\n            4=>'Apr',\n            5=>'May',\n            6=>'Jun',\n            7=>'Jul',\n            8=>'Aug',\n            9=>'Sep',\n            10=>'Oct',\n            11=>'Nov',\n            12=>'Dec');\n\n    /*** current month ***/\n    $selected = is_null($selected) ? date('m') : $selected;\n\n    $select = '<select name="'.$id.'" id="'.$id.'">'."\n";\n    foreach($months as $key=>$mon)\n    {\n        $paddedKey = str_pad($key, 2, "0", STR_PAD_LEFT);\n        $select .= "<option value=\"$paddedKey\"";\n        $select .= ($paddedKey==$selected) ? ' selected="selected"' : '';\n        $select .= ">$mon</option>\n";\n    }\n    $select .= '</select>';\n    return $select;\n}\n\n/********displays the month selection with current month selected/*******/\necho createMonths('Month', date('m'));\n\n/******DISPLAYS MONTH AS (04) --- @:( ******/\necho $_POST['Month'];