<?php\n// Get today's date in yyyy-mm-dd format\n$today = date('Y-m-d');\n\n// Create a connection to the database\n$servername = "your_server_name";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database_name";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check the connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// SQL query to check if today's date is within any date range\n$sql = "SELECT * FROM time_off_requests WHERE '$today' BETWEEN date_from AND date_to";\n\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    // Output data of each row\n    while($row = $result->fetch_assoc()) {\n        echo "Request ID: " . $row["id"]. " - Employee: " . $row["employee_name"]. " - Date From: " . $row["date_from"]. " - Date To: " . $row["date_to"]. "<br>";\n    }\n} else {\n    echo "No one has requested today off.";\n}\n\n// Close the connection\n$conn->close();\n?>