class pushNotifications {\n    private $sslPem = '/Users/jamey/Sites/cert.pem'; // Use absolute path\n\n    function connectToAPNS() {\n        $streamContext = stream_context_create();\n        stream_context_set_option($streamContext, 'ssl', 'local_cert', $this->sslPem);\n\n        $apnsServer = 'ssl://gateway.push.apple.com:2195';\n        $apns = stream_socket_client($apnsServer, $error, $errorString, 60, STREAM_CLIENT_CONNECT|STREAM_CLIENT_PERSISTENT, $streamContext);\n\n        if (!$apns) {\n            echo "Failed to connect: $error $errorString\n";\n            return false;\n        } else {\n            echo "Connected to APNS\n";\n        }\n        \n        // don't forget to close the connection\n        fclose($apns);\n    }\n}\n\n// Example usage\n$push = new pushNotifications();\n$push->connectToAPNS();