<?php\n\n// Initialize cURL session\n$ch = curl_init();\n\n// Set the URL for the login request\ncurl_setopt($ch, CURLOPT_URL, "https://api.rapidshare.com/cgi-bin/rsapi.cgi?sub=getaccountdetails&login=YOUR_LOGIN&password=YOUR_PASSWORD");\n\n// Set options to return the result as a string\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n\n// Execute the request\n$response = curl_exec($ch);\n\n// Check for errors\nif(curl_errno($ch)) {\n    echo 'Error:' . curl_error($ch);\n} else {\n    // Parse and use the response\n    // Assuming the response is in JSON format\n    $accountDetails = json_decode($response, true);\n    echo 'Traffic Left: ' . $accountDetails['traffic_left'] . '<br>';\n    echo 'Expiry Date: ' . $accountDetails['expiry_date'] . '<br>';\n}\n\n// Close the cURL session\ncurl_close($ch);\n?>