<?php\n   $dbHost = 'localhost';\n   $dbUsername = 'your_db_username';\n   $dbPassword = 'your_db_password';\n   $dbName = 'your_db_name';\n   $backupFile = 'backup-' . date("Y-m-d-H-i-s") . '.sql';\n   $command = "mysqldump --opt -h $dbHost -u $dbUsername -p$dbPassword $dbName > $backupFile";\n\n   system($command);\n\n   // Zip the backup file\n   $zip = new ZipArchive();\n   $zipFile = $backupFile . '.zip';\n   if ($zip->open($zipFile, ZipArchive::CREATE) === TRUE) {\n       $zip->addFile($backupFile);\n       $zip->close();\n       unlink($backupFile); // Delete the original .sql file\n   }\n\n   // Send the backup via email\n   $to = 'admin@example.com';\n   $subject = 'Database Backup';\n   $message = 'Find the backup attached.';\n   $headers = "From: no-reply@example.com";\n   $file = $zipFile;\n\n   $content = chunk_split(base64_encode(file_get_contents($file)));\n   $separator = md5(time());\n   $eol = "\r\n";\n\n   $headers .= "MIME-Version: 1.0" . $eol;\n   $headers .= "Content-Type: multipart/mixed; boundary=\"" . $separator . "\"" . $eol;\n   $headers .= "Content-Transfer-Encoding: 7bit" . $eol;\n   $headers .= "This is a MIME encoded message." . $eol;\n\n   $body = "--" . $separator . $eol;\n   $body .= "Content-Type: text/plain; charset=\"iso-8859-1\"" . $eol;\n   $body .= "Content-Transfer-Encoding: 8bit" . $eol;\n   $body .= $message . $eol;\n\n   $body .= "--" . $separator . $eol;\n   $body .= "Content-Type: application/octet-stream; name=\"" . basename($file) . "\"" . $eol;\n   $body .= "Content-Transfer-Encoding: base64" . $eol;\n   $body .= "Content-Disposition: attachment" . $eol;\n   $body .= $content . $eol;\n   $body .= "--" . $separator . "--";\n\n   mail($to, $subject, $body, $headers);\n\n   unlink($zipFile); // Delete the .zip file after sending\n   ?>