<?php\nif (isset($_POST['order'])) {\n    $order = $_POST['order'];\n    // Assuming you have a database connection $conn\n    // Save $order to the database for the logged-in user\n    $userId = 1; // Replace with the actual user ID\n    $orderJson = json_encode($order);\n    $stmt = $conn->prepare("UPDATE users SET portlet_order = ? WHERE id = ?");\n    $stmt->bind_param("si", $orderJson, $userId);\n    $stmt->execute();\n    $stmt->close();\n    echo "Order saved successfully";\n} else {\n    echo "No order received";\n}\n?>