amespace Drupal\your_module\Controller;\n\nuse Drupal\Core\Controller\ControllerBase;\nuse Symfony\Component\HttpFoundation\JsonResponse;\n\nclass AjaxContentController extends ControllerBase {\n  public function content() {\n    // Generate the content you want to return.\n    $content = [\n      '#markup' => '<p>This is the updated content via AJAX.</p>',\n    ];\n\n    // Return the content as a JSON response for AJAX.\n    return new JsonResponse(drupal_render($content));\n  }\n}