function processYouTubeVideos($url) {\n    // Extract the video ID from the URL\n    parse_str(parse_url($url, PHP_URL_QUERY), $params);\n    $videoId = isset($params['v']) ? $params['v'] : '';\n\n    // Return the embedded HTML code\n    if ($videoId) {\n        return '\n        <object width="400" height="245">\n            <param name="movie" value="http://www.youtube-nocookie.com/v/' . htmlspecialchars($videoId, ENT_QUOTES, 'UTF-8') . '&hl=en&fs=1&rel=0&showinfo=0"></param>\n            <param name="allowFullScreen" value="true"></param>\n            <param name="allowscriptaccess" value="always"></param>\n            <embed src="http://www.youtube-nocookie.com/v/' . htmlspecialchars($videoId, ENT_QUOTES, 'UTF-8') . '&hl=en&fs=1&rel=0&showinfo=0" type="application/x-shockwave-flash" allowscriptaccess="always" allowfullscreen="true" width="400" height="245"></embed>\n        </object>';\n    } else {\n        return ''; // Return empty if no video ID is found\n    }\n}\n\nfunction replaceYouTubeTags($str) {\n    return preg_replace_callback(\n        '/<youtube=([^>]*)>/i',\n        function ($matches) {\n            return processYouTubeVideos($matches[1]);\n        },\n        $str\n    );\n}\n\n// Example usage\n$input = '<p>My blog post...</p><youtube=http://www.youtube.com/watch?v=VIDEO_ID&feature=channel><p>More content...</p>';\n$output = replaceYouTubeTags($input);\necho $output;