// Step 1: Fetch all product IDs from the database\n$query = "SELECT id FROM product";\n$result = mysqli_query($connection, $query);\n\n$product_ids = [];\nwhile ($row = mysqli_fetch_assoc($result)) {\n    $product_ids[] = $row['id'];\n}\n\n// Step 2: Shuffle the IDs\nshuffle($product_ids);\n\n// Step 3: Paginate the shuffled IDs\n$page = isset($_GET['page']) ? (int)$_GET['page'] : 1;\n$items_per_page = 10; // Define the number of items per page\n$offset = ($page - 1) * $items_per_page;\n$paginated_ids = array_slice($product_ids, $offset, $items_per_page);\n\n// Step 4: Fetch the corresponding rows from the database\nif (!empty($paginated_ids)) {\n    $ids_string = implode(',', $paginated_ids);\n    $query = "SELECT * FROM product WHERE id IN ($ids_string) ORDER BY FIELD(id, $ids_string)";\n    $result = mysqli_query($connection, $query);\n\n    // Display the products\n    while ($row = mysqli_fetch_assoc($result)) {\n        // Output product data\n        echo "Product Name: " . $row['name'] . "<br>";\n        // Add more fields as necessary\n    }\n} else {\n    echo "No products found.";\n}